<?phpinclude '../config.php';if (isset($_POST['id']) && isset($_POST['name'])) {	$id = $_POST['id'];	$name = $_POST['name'];	$length = strlen($name);	// Connect to DB	$db = new DB_variable;		$results = "SELECT `red_val` FROM `".$db->db_data_table."` WHERE `id`=".$id;		$results = $db->send_query($results);		$results_row = mysql_fetch_assoc($results);	$red_value = $results_row['red_val'];		echo '<input id="contact_input" type="text" size="'.$length.'" value="'.$name.'" autocomplete="off">		  Mark when ><input id="red_set" class="settings" type="text" size="1" value="'.$red_value.'">days';}?>